function hasMention(message) {
  const userMentionRegex = /@U[a-zA-Z0-9]{20,}/;
  const allMentionRegex = /@all/;
  return userMentionRegex.test(message) || allMentionRegex.test(message);
}

function generateTextV2Object(message) {
  const mentionRegex = /@U[a-zA-Z0-9]{20,}/g;
  const mentions = [...new Set(message.match(mentionRegex) || [])];
  const substitution = {};

  // Replace user mentions with {userX}
  mentions.forEach((mention, index) => {
    const key = `user${index + 1}`;
    substitution[key] = {
      type: "mention",
      mentionee: {
        type: "user",
        userId: mention.slice(1) // remove '@'
      }
    };
  });

  // Replace @all with {everyone}
  if (message.includes("@all")) {
    substitution["everyone"] = {
      type: "mention",
      mentionee: {
        type: "all"
      }
    };
  }

  return substitution;
}

const inputMessage = $('Webhook From Scalify').first().json.body.message;

const substitution = generateTextV2Object(inputMessage);

const finalMessage = {
  "type" : "text",
  "text" : $('Clean Text').first().json.output
}

if (hasMention(inputMessage)) {
  finalMessage.type = "textV2";
  finalMessage["substitution"] = substitution;
}

return [
  {
    json: finalMessage
  }
];

const data = $('Webhook From Scalify').first().json.body;
const output = [];

