      text: data.Message
    }
  });
} else if (data['Type Message'] === "photo") {
  output.push({
    json: {
      type: "image",
      originalContentUrl: data['Image Message'][0],
      previewImageUrl: data['Image Message'][0]
    }
  });
}

return output;

const data = $('Webhook From Scalify').first().json.body;
const output = [];

if (data['Type Message'] === "text") {
  output.push({
    json: {
      type: "Custom",
      message: `[Auto-Reply] ${$('Webhook From Scalify').first().json.body.Message}`,
      contactId : $('Loop Over Contact').first().json.id,
